import React from 'react'
import { useChatContext } from 'stream-chat-react';
function JoinGame() {
  const [rivalUsername, setRivalUsername] = React.useState("");
  const { client } = useChatContext();
  const createChannel = async() =>{
    const response = await client.queryUsers({name: {$eq: rivalUsername}});
    if (response.users.length === 0) {
      alert("User not found");
      return;
    }
    const newChannel = await client.channel("messaging", {
      members: [client.userID, response.users[0].id],
      name: `Game between ${client.user.name} and ${response.users[0].name}`,
    })

  }
  return (
    <div className="joinGame"><h4>Create Game</h4>
    <input placeholder="username of rival" onChange = {(event) => {
      setRivalUsername(event.target.value);}
    }> </input>
    <button onClick ={createChannel}>Join/Start Game</button>
    </div>
  )
}

export default JoinGame
