import React from 'react'

function Game({ channel }) {
  const [playersJoined, setPlayersJoined] = React.useState(channel.state.watcherCount === 2);

  React.useEffect(() => {
    const handleWatcherChange = () => {
      setPlayersJoined(channel.state.watcherCount === 2);
    };
    channel.on("user.watching.start", handleWatcherChange);
    // for when player leaves
    channel.on("user.watching.stop", handleWatcherChange);
    // Check immediately in case watcherCount changed before effect ran
    setPlayersJoined(channel.state.watcherCount === 2);
    return () => {
      channel.off("user.watching.start", handleWatcherChange);
      channel.off("user.watching.stop", handleWatcherChange);
    };
  }, [channel]);

  if (!playersJoined) {
    return <div>Waiting for other player to connect</div>
  } else {
    return (
      <div>Game</div>
    )
  }
}

export default Game
