import React from 'react'

function Game({ channel }) {
  // Helper to get watcher count with fallback
  const getWatcherCount = () => {
    // Prefer watcherCount, fallback to counting watchers object
    return channel.state.watcherCount || Object.keys(channel.state.watchers || {}).length;
  };

  const [playersJoined, setPlayersJoined] = React.useState(false);

  React.useEffect(() => {
    const handleWatcherChange = () => {
      const count = getWatcherCount();
      console.log('Channel ID:', channel.id, 'Watcher count:', count);
      setPlayersJoined(count === 2);
    };

    // Query the channel state from the server on mount
    const fetchLatestState = async () => {
      try {
        await channel.query();
        handleWatcherChange();
      } catch (err) {
        console.error('Failed to query channel:', err);
      }
    };

    channel.on("user.watching.start", handleWatcherChange);
    channel.on("user.watching.stop", handleWatcherChange);
    channel.on("channel.updated", handleWatcherChange);

    fetchLatestState();

    return () => {
      channel.off("user.watching.start", handleWatcherChange);
      channel.off("user.watching.stop", handleWatcherChange);
      channel.off("channel.updated", handleWatcherChange);
    };
  }, [channel]);

  if (!playersJoined) {
    return <div>Waiting for other player to connect</div>
  } else {
    return (
      <div>Game (Channel ID: {channel.id})</div>
    )
  }
}

export default Game
