import React from 'react'
import { useChatContext, Channel } from 'stream-chat-react';
import Game from './Game';
function JoinGame() {
  const [rivalUsername, setRivalUsername] = React.useState("");
  const { client } = useChatContext();
  const [channel, setChannel] = React.useState(null);
  const createChannel = async() =>{
    const response = await client.queryUsers({name: {$eq: rivalUsername}});
    if (response.users.length === 0) {
      alert("User not found");
      return;
    }
    const userIds = [client.userID, response.users[0].id].sort();
    const channelId = `game-${userIds[0]}-${userIds[1]}`;
    const newChannel = await client.channel("messaging", channelId, {
      members: userIds,
      name: `Game between ${userIds[0]} and ${userIds[1]}`,
    });
    await newChannel.watch();
    setChannel(newChannel);
  }
  return (
    <>
    {channel ? (
      <Channel channel={channel} >
        <Game channel={channel}/>
      </Channel>
    ) : (
    <div className="joinGame"><h4>Create Game</h4>
    <input placeholder="username of rival" onChange = {(event) => {
      setRivalUsername(event.target.value);
    }} />
    <button onClick ={createChannel}>Join/Start Game</button>
    </div>
    )}
  </>
  )
}

export default JoinGame
